import { Avatar} from '@material-ui/core';
import React from 'react'
import { useConversations } from '../contexts/conversationsprovider';
import '../css/conversations.css'

export default function Conversations() {
    const {conversations,setSelectedConversation} =useConversations()


  return (
          <div className='Chats' >
              {conversations.map((conversation,index)=>
              {
                
                 return  ( <div key={index}  onClick={()=>setSelectedConversation(index)}  className='ChatsListItem'>
                     <Avatar/>
                     <div className='chatInfo'>
                     <span   className='chatName' ><h2>{conversation.participants.map(contact=>contact.name +',')}</h2> </span>
                     <span> {conversation.lastMessage.message} </span>
                     </div>
                 <br/>
                  </div>)
                    
                })}

                </div>
          

  )
}
