import React, { useRef } from 'react'
import '../css/chat.css'
import { useState ,useCallback} from 'react'
import { useConversations } from '../contexts/conversationsprovider';
import { useSocket} from '../contexts/socketprovider';
import { useUser} from '../contexts/userprovider';
import SendIcon from '@material-ui/icons/Send';
import {IconButton } from '@material-ui/core'
import InsertEmoticonIcon from '@material-ui/icons/InsertEmoticon';
import MicIcon from '@material-ui/icons/Mic';
import CloseIcon from '@material-ui/icons/Close';
import ImageIcon from '@material-ui/icons/Image';
import Picker from 'emoji-picker-react';
import axios from 'axios'


function SendImageBody(props) {


    const inputRef = useRef(null)
    const [Text,setText] = useState('')
    const {socket} = useSocket()
    const {info} = useUser()
    const {sendMessage,selectedConversation,currentConversationIsConnected,typingFlag,setTypingFlag} = useConversations()

    function handleSubmit(e)
    {
      e.preventDefault()
     // sendMessage(Text)
      setText(' ')
      props.backToChat()

    }

    function typing(e)
    {
        setText(e.target.value)
        if (socket.current == null) return;
        socket.current.emit("typing", {user:info,Conversation:selectedConversation})
    }

    return (
        <div className='SendImage'>
          <div className='imageDisplay'>
            <img className='imageShow' src= {props.imageURL}/>
          </div>
          <div className='addMessage'>

          <form onSubmit={handleSubmit} className='message_section'>
         <input className='message_input' ref={inputRef}  value={Text} onChange={typing} type='text' placeholder='type a message'/>
         <IconButton type='submit'>
            <SendIcon fontSize='large'/>
         </IconButton>
      </form>


          </div>

        </div>
    )
}

export default SendImageBody
