import React, { useState ,useEffect, useContext } from 'react'
import axios from 'axios'
const UserContext = React.createContext()

export function useUser() {
  return useContext(UserContext)
}

export  function UserProvider({ children }) {
  
  const [contacts, setContacts] = useState([])
  const [info,setInfo] =useState({})
  const [config,setConfig] =useState('')
  const[createdFlag,setCreatedFlag] =useState()

  useEffect(async ()=>
  {
      const response = await axios.get("http://localhost:5000/api/logIn/"+sessionStorage['id'],{'headers': {'x-access-token':sessionStorage['config']}});
      setInfo({id:response.data._id,name:response.data.name,phone:response.data.phone,ProfilePic:response.data.imageName})
      setContacts(response.data.contacts)
       
   }, [setInfo]);



  async function createContact(phone) {

    if(phone==info.phone)
    {
      console.log('cant Add yourself')
       return;
    }

    let checkIfExists = contacts.filter(contact=> contact.phone==phone)

    if(checkIfExists.length != 0)
    {
      console.log('already exists')
      return;
    }

   try{ 
      const response= await axios.get("http://localhost:5000/api/logIn/getByPhone/" + phone,{'headers': {'x-access-token':sessionStorage['config']}})
      const contact = {id:response.data._id,phone:response.data.phone,name:response.data.name,image:response.data.imageName}
      let newContacts = [...contacts,contact]
      setContacts(contacts)
      let UpdatedUser = info
      UpdatedUser={...info,contacts:newContacts}
       try{
        await axios.put("http://localhost:5000/api/logIn/" + sessionStorage['id'],UpdatedUser,{'headers': {'x-access-token':sessionStorage['config']}})
       } catch(err){console.log(err)}
     
    } catch(err){console.log(err)}
  }

  return (
    <UserContext.Provider value={{setContacts,setConfig,setInfo,info,config,contacts,createContact }}>
      {children}
    </UserContext.Provider>
  )
}
