import React, { useContext } from 'react'
import { useState } from 'react'
import { useContacts } from './contactsprovider'

const ConversationsContext = React.createContext()

export function useConversations() {
  return useContext(ConversationsContext)
}

export function ConversationsProvider({id, children }) {
  const {contacts} = useContacts()
  const [conversations,setConversations] =useState([])
  const [selectedConversationIndex,setSelectedConversationIndex] = useState(0)
  const [selectedConversationDetails,setSelectedConversationDetails] = useState()
  const [numberOfConversations,setNumberOfConversations] =useState(0)

  function createConversation(ids) 
  {
    let conversation= ids.map(id=>
    {
      return contacts.filter(contact=> id===contact.id)    
    })


      setConversations(prevConversations =>
      {
        return [...prevConversations,{id: numberOfConversations,participants:conversation[0],messages:[]}]
      })

      setNumberOfConversations(numberOfConversations +1)


  }

  function addMessageToConversation(currentConversation, text)
  {

    setConversations(prevConversations=>
      {
        let id=sessionStorage['id']
         let NewConversation =prevConversations.map(conversation=>
          {
            if(conversation.id === currentConversation.id)
            {
              return{...conversation,
                messages: [...conversation.messages , { id: id,message: text}]}
            }

            else return conversation;
          })

          return NewConversation;
      })


  }

  function sendMessage(currentConversation,text) {
    addMessageToConversation(currentConversation,text)

  }

  function setSelectedConversation(index)
  {
       setSelectedConversationIndex(index)
       setSelectedConversationDetails(conversations[index])
  }



  return (
    <ConversationsContext.Provider value={{ sendMessage,conversations,createConversation,setConversations,setSelectedConversation,selectedConversationDetails}}>
      {children}
    </ConversationsContext.Provider>
  )
}
