import React, { useState } from 'react'
import { useContacts } from '../contexts/contactsprovider'
import { useConversations } from '../contexts/conversationsprovider'
import {Button} from '@material-ui/core';
import '../css/addnew.css'

function AddContactConversation( closeModal) {

    const { contacts } = useContacts()
    const { createConversation,setCreateGroupFlag } = useConversations()
    
    function handleSubmit(e) {
        e.preventDefault()
        
        createConversation(e.target.value.id)
        closeModal()
      }
    
    return (
        <div className='add_new_border'>
        <button onClick ={ ()=> setCreateGroupFlag(true)}> create new group</button>
        {contacts.map(contact =>
          {return(

              <div  >
               <input
               onClick={() => handleSubmit}
               value={{id:contact.id,name:contact.name}}
              id={contact.name}/>
              {contact.name} <br/>
              </div>)}
          )}
          </div>
    )
}

export default AddContactConversation
