import '../css/sidebar.css'
import React from 'react'
import { useState } from 'react';
import ChatIcon from '@material-ui/icons/Chat';
import { Avatar, Button, IconButton, Modal } from '@material-ui/core';
import PeopleIcon from '@material-ui/icons/People';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Conversations from './conversations';
import Contacts from './contacts';
import AddNewContact from './addnewcontact'
import AddNewConversation from './addnewconversation'


export default function SideBar() {
    const [choosenAct,setChoosenAct]=useState('conversations')
    const [modalOpen, setModalOpen] = useState(false)

    function closeModal() {
        setModalOpen(false)
      }

    return (
        <div className='sideBar'>
            <div className='sidebar_top'>
                <Avatar/>
                <div className='sidebar_top_right'>
                    <IconButton onClick={()=>setChoosenAct('conversations')}>
                      <ChatIcon fontSize='large' />
                    </IconButton>
                    <IconButton  onClick={()=>setChoosenAct('contacts')}>
                      <PeopleIcon onClick={()=>setChoosenAct('contacts')} />
                    </IconButton>
                    <IconButton >
                      <MoreVertIcon />
                    </IconButton>
                </div>
            </div>
            <div className='sidebar_middle'>
                    <Button  onClick={() => setModalOpen(true)} className='add_new_Button' >
                        New {choosenAct==='conversations'? 'conversation' : 'contact'}
                    </Button>
                </div>
            <div className='sidebar_bottom'>
                {choosenAct==='contacts'? <Contacts/>:<Conversations/>}
            </div>

            
      <Modal show={modalOpen} onHide={closeModal}>
        {choosenAct === 'conversations' ?<AddNewConversation closeModal={closeModal} /> : <AddNewContact closeModal={closeModal} /> }
      </Modal>

        </div>
    )
}
