import React from 'react'
import { useState,useEffect} from 'react'
import { useConversations } from '../contexts/conversationsprovider';
import { useUser } from '../contexts/userprovider';
import { Avatar ,IconButton,makeStyles,Modal} from '@material-ui/core'
import ChatBody from './chatbody';
import SendImageBody from './sendimagebody';
import CloseIcon from '@material-ui/icons/Close';
import EditIcon from '@material-ui/icons/Edit';
import Dialog from '@material-ui/core/Dialog';

function ShowUserDetails({privateConversationHeader}) {

    const {selectedConversation,currentConversationIsConnected,typingFlag,createConversation,conversations,setSelectedConversation,
        setTypingFlag,showDetails,setShowDetails,UpdateConversation} = useConversations()
       const [imageFlag,setImageFlag] =useState(false)
       const [imageURL,setImageURL] =useState(false)
       const [EditGroupNameGlag,setEditGroupNameGlag]=useState(false)
       const classes = useStyles();
       const [pictureDialogOpen,setPictureDialogOpen]=useState(false)


    return (

        <div className='user_info'>
           <div onClick={()=>setPictureDialogOpen(true)}>
               <Avatar  src={selectedConversation.ConversationImage} style={{margin:'20px',height:'140px',width:'140px'}}/>
           </div>

           <Dialog className='open_picture_dialog' onClose={()=> setPictureDialogOpen(false)}  open={pictureDialogOpen}>
              <img src={selectedConversation.ConversationImage}/>
           </Dialog>

     
           <span className='name_and_lastSeen'>
              <span className='group_title'>
                 <h3>{selectedConversation.Name}</h3>
                 <IconButton onClick={()=>setEditGroupNameGlag(true)}>
                    <EditIcon style={{fontSize:'large',marginLeft:'10px'}}/>
                 </IconButton>
              </span>
     
          
               <span className='lastSeen' style={{padding:'10px',paddingLeft:'0px'}}>
                    {privateConversationHeader()}
               </span>
           </span>
       
      </div>

    )
}

export default ShowUserDetails
