import '../css/dashboard.css'
import React from 'react'
import SideBar from './sidebar'
import Chat from './chat'
import axios from 'axios'
import { useEffect } from 'react'
import { useConversations } from '../contexts/conversationsprovider'
import { useUser } from '../contexts/userprovider'

export default function Dashboard({id}) {
    const {selectedConversationDetails} =useConversations()
    const {setInfo,setContacts,setConfig} =useUser()
    
    useEffect(async() =>{

        try{ 
          let response = await axios.get("http://localhost:5000/api/logIn/" + sessionStorage['id'],config)
          setInfo({id:response.data.User._id,name:response.data.User.name,phone:response.data.User.phone,ProfilePic:response.data.User.imageName,contacts:response.data.User.contacts})
          setContacts(response.data.User.contacts)
          setConfig({'headers': {'x-access-token': response.data.token }})


          } catch(err){console.log(err)}
      }, [])
    

    return (
        <div className='dashboard'>
           <div className='dashboard_body'>
              <SideBar id={id}/>
              {selectedConversationDetails!==undefined?  <Chat id={id}  />: ''}
           </div>
        </div>
    )
}
