import {useState} from 'react'
import '../css/login.css'
import axios from 'axios'
import {useHistory} from 'react-router-dom'
import path from 'path'

 

function Register(props) {
   
    const [phone,setPhone] = useState('');
    const [UserName,setUserName] = useState('');
    const [Picture,setPicture] =useState('')
    const [Error,setError] = useState("")
    const history=useHistory();
    const [UploadedFile,setUploadedFile] = useState()




    /*get user information and token from DB*/
    async function handleSubmit(e)
    {
        e.preventDefault()

       

        const formData= new FormData();
        formData.append('file',Picture)

        

        const fileResponse =await axios.post("http://localhost:5000/api/logIn/AddProfilePic",formData,{headers: {
              'Content-Type': 'multipart/form-data'}})

        const {fileName,filePath} =fileResponse.data

        let picturePath ='uploads/'+fileName
        
        let obj = {name: UserName,phone: phone,ProfilePicName:picturePath}

        const response = await axios.post("http://localhost:5000/api/logIn/Register",obj)


        if(response.status== 'ok')
          
           sessionStorage["token"]  =response.data.token
           sessionStorage["name"]=response.data.User.name
           sessionStorage["phone"]=response.data.User.phone
           sessionStorage["ProfilePic"]=response.data.User.imageName
           sessionStorage["authenticate"] = true

           props.GetLoginID(phone)

           history.push('/App')
    }
        
    



    function handleFileUpload(e) {
        setPicture(e.target.files[0])
      }

    return (
        <div className="LogInPage">
             <form onSubmit={handleSubmit} className='logInBorder'>
             enter your phone:  
              <input type='text' onChange={(e)=> setPhone(e.target.value)}/> 
              enter your name: 
              <input type='text' onChange={(e)=> setUserName(e.target.value)}/>  
              
              <input type="file" onChange={handleFileUpload} />
                <button type='submit'> Sign Up</button>
            </form>     
        </div>

    )
}

export default Register
