import React, { useContext ,useCallback, useRef} from 'react'
import { useState,useEffect } from 'react'
import { useUser } from './userprovider'
import { useSocket } from './socketprovider'
import axios from 'axios'
import { useForkRef } from '@material-ui/core'

const ConversationsContext = React.createContext()

export function useConversations() {
  return useContext(ConversationsContext)
}

export function ConversationsProvider({id, children }) {
  const {contacts,info} = useUser()
  const [conversations,setConversations] =useState([])
  const [selectedConversation,setSelectedConversation] = useState()
  const RefConversations =useRef(conversations)
  const currentConversationRef=useRef(selectedConversation)
  const [createGroupFlag,setCreateGroupFlag] =useState(false)
  const socket = useSocket()
  const config= {'headers': {'x-access-token':sessionStorage['config']}};

  useEffect(async () =>
  {

    try
    {
      let response = await axios.get("http://localhost:5000/api/conversations/UserConversations/"+sessionStorage['id'],config)
      let ConversationsList =  response.data.map(conversation=>
      {
        if(conversation.Participants.length == 1 && conversation.Name == sessionStorage['name'] )
        {
          let UpdatedNameConversation = conversation
          UpdatedNameConversation= {...UpdatedNameConversation,Name:conversation.Participants[0].name}
  
          return UpdatedNameConversation
        }
  
        else return conversation
          
      })
   /*  if(ConversationsList.length>0)
      setSelectedConversation(ConversationsList[0])*/
        
      setConversations(ConversationsList)
  
     }catch(err){console.log(err)}


  },[]) 


async function createConversation(ids,name,image) 
  {
    
    let  ConversationImage= image
    let isGroup = false

    //no participants chosen
    if(ids.length == 0)
    {
     console.log('no participants choosen')
     return;
    }


    //check if conversation already exists
    let ConversationExists = null
    if(ids.length == 1)
    {
      ConversationExists = conversations.find(conversation => conversation.Name ==name)
    }

    if(ConversationExists)
      setSelectedConversation(ConversationExists)


    
      //create new conversation
    else
    {
      //get conversation participants
      const participants =ids.map(id=>
      { 
        let  addContactToConversation =contacts.filter(contact=> id === contact.id)    
        return addContactToConversation[0]
      })
      
      //add creator to participants
      participants.push({id:info.id,name:info.name,phone:info.phone,image:info.ProfilePic})
    
      //if group
      if(ids.length > 1)
      {
        const formData= new FormData();
        formData.append('file',ConversationImage)
        const fileResponse =await axios.post("http://localhost:5000/api/conversations/AddConversationPic",formData,{headers: {
        'Content-Type': 'multipart/form-data'}})
        if(fileResponse.data.msg == 'file uploaded')
        {
        const {fileName,filePath} =fileResponse.data
        ConversationImage='uploads/'+fileName
        }
        else ConversationImage = 'no image'
        isGroup = true

      }

      let newConversation = {Name:name,creatorId: sessionStorage['id'],Participants:participants,Messages:[],LastMessage:{id:'',sender:'',message:''},ConversationImage:ConversationImage,isGroup}
   
      //updateDB
      try
      {
        let Response = await axios.post("http://localhost:5000/api/conversations",newConversation,config)
        if(Response.data.status == "created")
        {
           console.log(Response.data.conversation)
          setSelectedConversation(Response.data.conversation)

          //show conversation only if messages sent
          if(Response.data.conversation.Messages.length>0)
             setConversations(prevConversations => { return [...prevConversations, Response.data.conversation]})
       // }
       }
    } catch(err){console.log(err)}
    
   }
  }



const addMessageToConversation = useCallback(async ({UpdatedConv}) =>
{
    let ConversationExists =false
        let newListOfConversations =  RefConversations.current.map(conversation=>
        {
            
            if(conversation._id === UpdatedConv._id)
            {
              ConversationExists =true
              let newConv = {...conversation, Messages:UpdatedConv.Messages,LastMessage:UpdatedConv.LastMessage}
              if(currentConversationRef.current)
              {
                 if(currentConversationRef.current._id === UpdatedConv._id)
                   setSelectedConversation(newConv)
              }

             
              return newConv
            }
            else return conversation
          })

          console.log(newListOfConversations)



      if(!ConversationExists)
      {

        if(!UpdatedConv.isGroup)
        {
          let newConversation = {...UpdatedConv,Name:UpdatedConv.Participants[0].name,image: UpdatedConv.Participants[0].image}
          setConversations(prevConversations=> [...prevConversations,newConversation])
        }

        else  setConversations(prevConversations=> [...prevConversations,UpdatedConv])
      }
      else setConversations(newListOfConversations)

},[setConversations])

useEffect(()=>
{
    
   if (socket.current == null) return

   RefConversations.current = conversations
   currentConversationRef.current=selectedConversation
   socket.current.on('receive-message',addMessageToConversation)

   return () => socket.current.off('receive-message',(addMessageToConversation))

},[conversations,selectedConversation])


const updateSenderConversation= (AddMessage) =>
{
   let ConversationExists = false
   setSelectedConversation(AddMessage)

      let newListOfConcversations= conversations.map(conversation =>
        {
           if(conversation._id == AddMessage._id)
           {
             ConversationExists = true
             return AddMessage
           }
           else return conversation
        })

    if(ConversationExists)
    {
      setConversations(newListOfConcversations)
    }

    else setConversations(prevConversations => [...prevConversations,AddMessage])


}
  function sendMessage(text) 
  {
      let CurrentMessage = {id:info.id,name:info.name,message:text}
      let sender = {id: info.id, phone:info.phone,name:info.name ,image:info.ProfilePic}
      let AddMessage = {...selectedConversation,Messages:[...selectedConversation.Messages,CurrentMessage],LastMessage:CurrentMessage}
      socket.current.emit('send-message',{sender:sender,UpdatedConversation:AddMessage,conversationId:selectedConversation._id})

      updateSenderConversation(AddMessage)

  }







  return (
    <ConversationsContext.Provider value={{createGroupFlag,setCreateGroupFlag,sendMessage,conversations,createConversation,setConversations,setSelectedConversation,selectedConversation}}>
      {children}
    </ConversationsContext.Provider>
  )
}
