import '../css/chat.css'
import React, { useRef } from 'react'
import { useState ,useEffect} from 'react'
import { useConversations } from '../contexts/conversationsprovider';
import { Avatar, IconButton } from '@material-ui/core'
import InsertEmoticonIcon from '@material-ui/icons/InsertEmoticon';
import MicIcon from '@material-ui/icons/Mic';




export default function Chat() {

    const [Text,setText] = useState('')
    const {sendMessage,selectedConversationDetails} = useConversations()
    const lastMessageRef= useRef(null)
    const setRef = useCallback((node) => {
        if(node)
          node.scrollIntoView({smooth:true})
        },[])

    useEffect(()=>
    {
        if(lastMessageRef.current)
          lastMessageRef.current.scrollIntoView({smooth:true})

    },[lastMessageRef.current])

    function handleSubmit(e)
    {
      e.preventDefault()
      sendMessage(selectedConversationDetails,Text)
      setText(' ')
    }

    function getChatName()
    {
        let roomName=''
          selectedConversationDetails.participants.map((participant,index) =>
            {
                if(selectedConversationDetails.participants.length-1 > index)
                  roomName= roomName+ ' '+ participant.name + ',';
                else 
                roomName = roomName +participant.name
                
            })
        return roomName
    }

    return (
        <div className='chat'>
            <div className='chat_header'>
                <Avatar/>
                <div className='chat_header_info'>
                   <h2> {getChatName()} </h2> 
                   <p> last seen...?</p>
               </div>
           </div>

           <div className='chat_body'>
               {selectedConversationDetails.messages.map((message,index)=>
               {
                   const lastMessage = selectedConversationDetails.messages.length -1 === index
                   let sender = message.fromMe==true? 'chat_message' : ' chat_message chat_reciever'

                   return (
                  
                    <p className={sender} ref={lastMessage ? setRef : null}>

                   <span className='sender_name'>{message.name}</span>
                   <span className='message_content'>{message.message}</span> </p>
              
                   )}
                )}
 
           </div>

           <div className='chat_footer'>
               <IconButton>
                 <InsertEmoticonIcon fontSize='large'/>
                </IconButton>
                 <form onSubmit={handleSubmit} className='message_section'>
                    <input className='message_input' value={Text} onChange={(e)=>setText(e.target.value)} type='text' placeholder='type a message'/>
                    <button type='submit' > send </button>
                 </form>
                 <IconButton>
                  <MicIcon fontSize='large'/>
               </IconButton>
           </div>
        </div>
    )
}
