import React, { useState } from 'react'
import { useUser } from '../contexts/userprovider'
import { useConversations } from '../contexts/conversationsprovider'
import {Button,Avatar} from '@material-ui/core';
import '../css/addnew.css'

function AddContactConversation( { closeModal }) {

    const { contacts } = useUser()
    const { createConversation,setCreateGroupFlag } = useConversations()
    
    function handleSubmit({id,name,img}) 
    {
      let ids=[id]
      createConversation(ids,name,img)
      closeModal()
    }
    
    return (
        <div className='add_new_border'>
        <Button onClick ={ ()=> setCreateGroupFlag(true)}> create new group</Button>

        {contacts.map(contact =>
          {return(
              <div  onClick={()=>handleSubmit({id:contact.id,name:contact.name, image:contact.imageName})} className='ChatsListItem'>
                 <Avatar src={process.env.PUBLIC_URL + contact.image}/>
                 <div className='chatInfo'> <h2>{contact.name} </h2> </div>
               </div>)})}

          </div>
    )
}

export default AddContactConversation
