import React from 'react'

function Message({sender,message}) {

    const [modalOpen, setModalOpen] = useState(false)

    function openImage(URL)
    {
      setModalOpen(true)
      setSelectedImageURL(URL)
      const [SelectedImageURL,setSelectedImageURL] =useState(null)

    }


    return (
        <div>      
           <p className={sender} ref={lastMessage ? setRef : null}>
              <span className='sender_name'>{message.name}</span>
              {message.containsImage? 
              <img className='image_in_message' src={message.imageURL} onClick={()=>openImage(message.imageURL)} width='200px' height='200px' /> 
              : ''}
  
          
            <p className='message_time_and_content'><span className='message_content'>{message.message}</span> 
            <span className='message_time'>{(message.timeSent).substring(11,17)}</span> </p>
    
             </p> 

             <Dialog onClose={()=> setModalOpen(false)}  open={modalOpen}>
                <img src={SelectedImageURL}  height='500px' width='500px'/>
            </Dialog>
        </div>
    )
}

export default Message
