import React, { useState } from 'react'
import { useContacts } from '../contexts/contactsprovider'
import { useConversations } from '../contexts/conversationsprovider'

function CreateNewGroup( closeModal) {

    const [selectedContactsId, setSelectedContactsId] = useState([])
    const { contacts } = useContacts()
    const { createConversation } = useConversations()
    const [createGroupFlag,setCreateGroupFlag] =useState(false)

    function handleSubmit(e) {
        e.preventDefault()
    
        createConversation(selectedContactsId)
        closeModal()
      }
    

    function handleCheckboxChange(contactId) {
        if(selectedContactsId.includes(contactId))
        {
            setSelectedContactsId(prevSelectedContactsId =>
             {
                 return (prevSelectedContactsId.filter(id=> id!==contactId))
             } )
        }
  
        else {
            setSelectedContactsId(prevSelectedContactsId=>
              {
                  return [...prevSelectedContactsId,contactId]
              })
          }
           
    }
    return (
        <div>
          {contacts.map(contact =>
            {return(

                <div>
                 <input
                type="checkbox"
                value={selectedContactsId.includes(contact.id)}
                id={contact.name}
                onChange={() => handleCheckboxChange(contact.id)}/>
                {contact.name} <br/>
                </div>)}
            )}
            <Button type="submit" onClick={handleSubmit}>Create Group</Button>
            
            <Button type="submit" onClick={()=>setCreateGroupFlag(false)}>Add contact</Button>
        </div>
    )
}

export default CreateNewGroup
