import '../css/sidebar.css'
import React from 'react'
import { useState } from 'react';
import ChatIcon from '@material-ui/icons/Chat';
import { Avatar, IconButton } from '@material-ui/core';
import PeopleIcon from '@material-ui/icons/People';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Conversations from './conversations';
import Contacts from './contacts';

export default function SideBar() {
    const [choosenAct,setChoosenAct]=useState('conversations')

    return (
        <div className='sideBar'>
            <div className='sidebar_top'>
                <Avatar/>
                <div className='sidebar_top_right'>
                    <IconButton onClick={()=>setChoosenAct('conversations')}>
                      <ChatIcon fontSize='large' />
                    </IconButton>
                    <IconButton  onClick={()=>setChoosenAct('contacts')}>
                      <PeopleIcon onClick={()=>setChoosenAct('contacts')} />
                    </IconButton>
                    <IconButton >
                      <MoreVertIcon />
                    </IconButton>
                </div>
            </div>
            <div className='sidebar_bottom'>
                {choosenAct==='contacts'? <Contacts/>:<Conversations/>}
                <div className='sidebar_bottom_end'>
                    <Button >
                        New {choosenAct==='conversations'? 'conversation' : 'contact'}
                    </Button>
                </div>
            </div>
        </div>
    )
}
