import '../css/chat.css'
import React, { useRef } from 'react'
import { useState ,useEffect,useCallback} from 'react'
import { useConversations } from '../contexts/conversationsprovider';
import { useSocket} from '../contexts/socketprovider';
import { useUser} from '../contexts/userprovider';
import { Avatar, IconButton, useForkRef } from '@material-ui/core'
import InsertEmoticonIcon from '@material-ui/icons/InsertEmoticon';
import MicIcon from '@material-ui/icons/Mic';
import CloseIcon from '@material-ui/icons/Close';
import ImageIcon from '@material-ui/icons/Image';
import Picker from 'emoji-picker-react';
import axios from 'axios'
import ChatBody from './chatbody';





export default function Chat() {

    const [Text,setText] = useState('')
    const {socket} = useSocket()
    const {info} = useUser()
    const {sendMessage,selectedConversation,currentConversationIsConnected,typingFlag,setTypingFlag} = useConversations()
    const inputRef = useRef(null)
  
    const [emojiFlag,setEmojiFlag] = useState(false)
    const [imageFlag,setImageFlag] =useState(false)
   

    
    const groupheader = () =>
    {
      let returnValue = ''
      console.log(typingFlag)
      if(typingFlag != '')
      {
        returnValue = <div className='lastSeen'> {typingFlag} is typing...</div>
        setTimeout(()=> setTypingFlag(''),1000)
      }

      else
      {
        let UsersInGroup = ''
        selectedConversation.Participants.forEach((participant,index)=> 
        {
            if(index ===  selectedConversation.Participants.length-1 )
                UsersInGroup = UsersInGroup + participant.name
            else
                UsersInGroup= UsersInGroup + participant.name + ', '
        })
            
        returnValue=  <div className='lastSeen'>{UsersInGroup} </div>

      }

      return returnValue
 
 }
    

    const privateConversationHeader = () =>
    {
        let returnValue = ''
       if(currentConversationIsConnected == '')
       {
        if(typingFlag != '')
        {
            returnValue = <div className='lastSeen'> typing...</div>
            setTimeout(()=> setTypingFlag(''),1000)

        }

        else returnValue = <div className='connected'><div className='circle'></div> online </div>
            
       }
       else returnValue = <div className='lastSeen'>{currentConversationIsConnected}</div>


       return returnValue

    }

    function typing(e)
    {
        setText(e.target.value)
        if (socket.current == null) return;
        socket.current.emit("typing", {user:info,Conversation:selectedConversation})
    }

    function handleSubmit(e)
    {
      e.preventDefault()
      sendMessage(Text)
      setText(' ')
    }

    async function handleImage(e)
    {
      const data = new FormData()
      data.append('file',e.target.files[0])
      data.append("upload_preset","whatsApp_clone")
      data.append("cloud_name","dsrgpqnyv")
      try{
      let response = await axios.post("https://api.cloudinary.com/v1_1/dsrgpqnyv/image/upload",data)
      setImageURL(response.data.url)
      setImageFlag(true)
      }catch(err){console.log(err)}
    }

    const onEmojiClick = (event, emojiObject) => {
        setText(Text + emojiObject.emoji)
      };




    return (
        <div className='chat'>
            <div className='chat_header'>
                
                <Avatar src={process.env.PUBLIC_URL +selectedConversation.ConversationImage}/>
                <div className='chat_header_info'>
                   <h2> {selectedConversation.Name} </h2> 
                   {selectedConversation.isGroup?  groupheader() : privateConversationHeader()}
               </div>
           </div>
    
            <ChatBody/>
 
           <div className='chat_footer_with_stickers'>
           {emojiFlag?  <Picker onEmojiClick={(e,emojiObject)=>onEmojiClick(e,emojiObject)} pickerStyle={{ width: '100%' }}/>: ''}
           <div className='chat_footer'>
           {emojiFlag?   <IconButton  onClick={()=>setEmojiFlag(false)}>
                      <CloseIcon fontSize='large'/>
                      </IconButton>:''
                    }
               <IconButton  onClick={()=>setEmojiFlag(true)}>
                 <InsertEmoticonIcon fontSize='large'/>
                </IconButton>
           
                <input accept="image/*" className='invisibleInput' id="icon-button-file" type="file"  style={{ visibility: 'hidden'}} onChange={handleImage}/>
                <label htmlFor="icon-button-file">
                 <IconButton component="span">
              < ImageIcon  fontSize='large'></ImageIcon>
               </IconButton>
  </label> 

                 <form onSubmit={handleSubmit} className='message_section'>
                    <input className='message_input' ref={inputRef}  value={Text} onChange={typing} type='text' placeholder='type a message'/>
                    <button type='submit' > send </button>
                 </form>
                 <IconButton>
                  <MicIcon fontSize='large'/>
               </IconButton>
               </div>
           </div>

        </div>
    )
}
