import React, { useState ,useEffect, useContext } from 'react'
import axios from 'axios'
import { useSocket } from "./socketprovider";
import Contacts from '../components/contacts';
const UserContext = React.createContext()

export function useUser() {
  return useContext(UserContext)
}

export  function UserProvider({ children })
{
  const [contacts, setContacts] = useState([])
  const [info,setInfo] =useState({})
  const config= {'headers': {'x-access-token':sessionStorage['config']}}
  const {ConnectedUsers } = useSocket();
  

  /*useEffect(async ()=>
  {
      const response = await axios.get("http://localhost:5000/api/logIn/"+sessionStorage['id'],config);
      setInfo({id:response.data._id,name:response.data.name,phone:response.data.phone,imageName:response.data.imageName,LastSeen:response.data.LastSeen})
 
       
   }, [setInfo]);*/

   function getUpdatedContacts(contactsList)
   {

    return Promise.all(contactsList.map(async (contact)=> 
    {
      let newContact = null
      let userIsConnected = ConnectedUsers.some(user => user.userId == contact.id)
      if(userIsConnected)
      {
        contact.connected= true  
        newContact=contact;
      }

      else
      {
        const contactResponse = await axios.get("http://localhost:5000/api/logIn/"+contact.id,config)
          let updatedContact= {...contact,id:contactResponse.data._id,name:contactResponse.data.name,phone:contactResponse.data.phone,
            imageName:contactResponse.data.imageName,LastSeen:contactResponse.data.LastSeen,connected:false}
         newContact= updatedContact;
      
      }    
      
     if(newContact)
      return newContact

    }))
   }

   useEffect(async () =>
   {

    if(ConnectedUsers.length>0)
    {
    const response = await axios.get("http://localhost:5000/api/logIn/"+sessionStorage['id'],config);
    setInfo({id:response.data._id,name:response.data.name,phone:response.data.phone,imageName:response.data.imageName,LastSeen:response.data.LastSeen})
    console.log('in users provider')
    console.log(info)
    console.log(sessionStorage['id'])
    let userContacts = response.data.contacts
    getUpdatedContacts(userContacts).then(res=>setContacts(res))
    }

   },[ConnectedUsers])


  const createContact = async(phone,inListFlag) =>
  {

    console.log(info)
    if(phone==info.phone)
    {
      console.log('cant Add yourself')
       return;
    }

    let checkIfExists = contacts.filter(contact=> contact.phone==phone)

    if(checkIfExists.length != 0)
    {
      console.log('already exists')
      return;
    }

    try{ 

      const response= await axios.get("http://localhost:5000/api/logIn/getByPhone/" + phone,config)



      if(response != 'no such user')
      {
        
       const contact = {id:response.data._id,phone:response.data.phone,name:response.data.name,imageName:response.data.imageName,LastSeen:response.data.LastSeen,inContactList:inListFlag}
       let contactConnected = ConnectedUsers.some(user=> user.userId == contact.id)
       if(contactConnected)
       {
         contact.connected=true;
       }
       else contact.connected=false
       let newContacts = [...contacts,contact]
       setContacts(prevContacts=> {return [...prevContacts,contact]})
       let UpdatedUser = {name:info.name,phone:info.phone,imageName:info.imageName,LastSeen:info.LastSeen}
       UpdatedUser={...info,contacts:newContacts}
       console.log(UpdatedUser)
       try{
         await axios.put("http://localhost:5000/api/logIn/" + sessionStorage['id'],UpdatedUser,{'headers': {'x-access-token':sessionStorage['config']}})
       } catch(err){console.log(err)}
      }
      else console.log('user dosent exist')
     
    } catch(err){console.log(err)}
  }



  return (
    <UserContext.Provider value={{setContacts,setInfo,info,config,contacts,createContact }}>
      {children}
    </UserContext.Provider>
  )
}
