import React,{useState} from 'react'
import '../css/login.css'
import axios from 'axios'
import {Link,useHistory} from 'react-router-dom'
import { useContacts } from '../contexts/contactsprovider';

import path from 'path'



export default function LogIn(props) {

    const [phone,setPhone] = useState('');
    const [UserName,setUserName] = useState('');
    const {setContacts} = useContacts()

    const [Error,setError] = useState('')
    const history=useHistory();



    /*get user information and token from DB*/
    async function handleSubmit(e)
    {
        setError('')
        e.preventDefault()
        let obj = {name: UserName,phone: phone}
        let response = await axios.post("http://localhost:5000/api/logIn",obj)

        if(response.data !== "not found")
        {
           sessionStorage['userData'] = {id:response.data._id,token:response.data.token,name:response.data.User.name,phone:response.data.User.phone,ProfilePic:response.data.User.imageName,contacts:response.data.User.contacts,authenticate:true}

           setContacts(user.data.User.contacts)
            
           props.GetLoginID(phone)

           history.push('/App')
        }
        
        else
           setError("One or more of your identification details is incorrect.")
    }

 

    return (
        <div className="LogInPage">
            <form onSubmit={handleSubmit} className='logInBorder'>
             enter your phone:  
              <input type='text' onChange={(e)=> setPhone(e.target.value)}/> 
              enter your name: 
              <input type='text' onChange={(e)=> setUserName(e.target.value)}/>  
              <button type='submit'> Login</button>
              <button  ><Link className='defaultLink' to={`/Register`}>Register</Link></button>
              {Error}
               
            </form>     
        </div>
    )
}
