import React, { useRef } from 'react'
import { useState ,useCallback,useEffect} from 'react'
import { useConversations } from '../contexts/conversationsprovider';
import { useSocket} from '../contexts/socketprovider';
import { useUser} from '../contexts/userprovider';
import {IconButton } from '@material-ui/core'
import InsertEmoticonIcon from '@material-ui/icons/InsertEmoticon';
import MicIcon from '@material-ui/icons/Mic';
import CloseIcon from '@material-ui/icons/Close';
import ImageIcon from '@material-ui/icons/Image';
import Picker from 'emoji-picker-react';
import Dialog from '@material-ui/core/Dialog';
import { useVoiceRecorder } from "use-voice-recorder";
import axios from 'axios'




function ChatBody(props) {

    const [Text,setText] = useState('')
    const {socket} = useSocket()
    const {info} = useUser()
    const {sendMessage,selectedConversation,currentConversationIsConnected,typingFlag,setTypingFlag} = useConversations()
    const inputRef = useRef(null)
  
    const [emojiFlag,setEmojiFlag] = useState(false)
    const [modalOpen, setModalOpen] = useState(false)
    const [imageFlag,setImageFlag] =useState(false)
    const [imageURL,setImageURL] =useState(null)
    const [SelectedImageURL,setSelectedImageURL] =useState(null)
    let audio ='' 

    const [recordFlag,setRecordFlag] = useState(true)
    const [firstTime,setFirstTime] = useState(true)
    const [record, updateRecord] = useState([]);
    const {isRecording, stop, start} = useVoiceRecorder((data) => {
      updateRecord( window.URL.createObjectURL(data));
    });

    useEffect(()=>
    { 
  
      if(recordFlag==true)
      {
        
        start()
      }
        
      else
      {
        console.log('here')
        if(!firstTime)
        {
        stop()
        console.log(record)
        }
        else setFirstTime(true)
      }


    },[recordFlag])



    const setRef = useCallback((node) => 
    {
      if(node)
         node.scrollIntoView({smooth:true})
    },[])

    function typing(e)
    {
        setText(e.target.value)
        if (socket.current == null) return;
        socket.current.emit("typing", {user:info,Conversation:selectedConversation})
    }

    function handleSubmit(e)
    {
      e.preventDefault()
      sendMessage(Text,false)
      setText(' ')
    }

    async function handleImage(e)
    {
      const data = new FormData()
      data.append('file',e.target.files[0])
      data.append("upload_preset","whatsApp_clone")
      data.append("cloud_name","dsrgpqnyv")
      try{
      let response = await axios.post("https://api.cloudinary.com/v1_1/dsrgpqnyv/image/upload",data)
      setImageURL(response.data.url)
      setImageFlag(true)

      props.imageCallback(response.data.url)
      }catch(err){console.log(err)}
    }

    function openImage(URL)
    {
      setModalOpen(true)
      setSelectedImageURL(URL)

    }

    

    const onEmojiClick = (event, emojiObject) => {
        setText(Text + emojiObject.emoji)
      };



    return (
        <div className= 'body_and_footer'>
        <div className='chat_body'>
        {selectedConversation.Messages.map((message,index)=>
        {
            const lastMessage = selectedConversation.Messages.length -1 === index
            let sender = message.id==sessionStorage['id']? 'chat_message' : ' chat_message chat_reciever'

            return (
            <div>
             <p className={sender} ref={lastMessage ? setRef : null}>
            <span className='sender_name'>{message.name}</span>
            {message.containsImage? 
            <img className='image_in_message' src={message.imageURL} onClick={()=>openImage(message.imageURL)} width='200px' height='200px' /> 
            : ''}
          
            <p className='message_time_and_content'><span className='message_content'>{message.message}</span> 
            <span className='message_time'>{(message.timeSent).substring(11,17)}</span> </p>
    
             </p> 

             <Dialog onClose={()=> setModalOpen(false)}  open={modalOpen}>
                <img src={SelectedImageURL}  height='500px' width='500px'/>
            </Dialog>
            
          
             </div>
       
            )}

     
         )}

    </div>

   <div className='chat_footer_with_stickers'>
      {emojiFlag?  <Picker onEmojiClick={(e,emojiObject)=>onEmojiClick(e,emojiObject)} pickerStyle={{ width: '100%' }}/>: ''}
      <div className='chat_footer'>
      {emojiFlag? <IconButton  onClick={()=>setEmojiFlag(false)}><CloseIcon fontSize='large'/> </IconButton>:''}
      <IconButton  onClick={()=>setEmojiFlag(true)}>
         <InsertEmoticonIcon fontSize='large'/>
      </IconButton>
      <input accept="image/*" className='invisibleInput' id="icon-button-file" type="file"  style={{ visibility: 'hidden'}} onChange={handleImage}/>
      <label htmlFor="icon-button-file">
       <IconButton component="span">
         < ImageIcon  fontSize='large'></ImageIcon>
       </IconButton>
      </label> 

      <form onSubmit={handleSubmit} className='message_section'>
         <input className='message_input' ref={inputRef}  value={Text} onChange={typing} type='text' placeholder='type a message'/>
         <button type='submit' > send </button>
      </form>
      <IconButton>
         <MicIcon fontSize='large' onClick={setRecordFlag(!recordFlag)}/>
      </IconButton>
      {audio}
    </div>
</div>

</div>

    
    )
}

export default ChatBody
