import { useRef } from 'react'
import {Button} from '@material-ui/core';
import '../css/addnew.css'
import axios from 'axios'
import { useContacts } from '../contexts/contactsprovider';


export default function AddNewContact({closeModal}) {

   const idRef = useRef()
   const {createContact} = useContacts()
   

  async function handleSubmit()
  {
    const response = await axios.post("http://localhost:5000/api/logIn/" + idRef.current.value,obj)
    createContact({id:response.data.phone,name:response.data.name,image:response.data.imageName})

    closeModal();
  }

  return (
   
   <div className='add_new'>
            <div className='add_new_border'>
                <span>ID</span>
                <input type='text' ref={idRef} required/>
                <Button onClick={handleSubmit}> Add Contact</Button>
                


            </div>
          
               
 
        </div>

  )
}
