import {useState} from 'react'
import '../css/login.css'
import axios from 'axios'
import {useHistory} from 'react-router-dom'
import {useUser} from '../contexts/userprovider'

 

function Register({CanLogIn}) {
   
  
    const history=useHistory();
    const {setInfo,setConfig} =useUser()
    const[phone,setPhone] =useState('')
    const[name,setName] =useState('')
    const [Picture,setPicture] =useState()

    /*get user information and token from DB*/
    async function handleSubmit(e)
    {
        e.preventDefault()

        const formData= new FormData();
        formData.append('file',Picture)


        const fileResponse =await axios.post("http://localhost:5000/api/logIn/AddProfilePic",formData,{headers: {
              'Content-Type': 'multipart/form-data'}})

        const {fileName,filePath} =fileResponse.data

        let picturePath ='uploads/'+fileName
        
        let obj = {name: name,phone: phone,ProfilePicName:picturePath,contacts:[]}

        const response = await axios.post("http://localhost:5000/api/logIn/Register",obj)


        if(response.status== 'ok')
          {
            sessionStorage['config']= response.data.token 
            sessionStorage['id'] = response.data.User._id
            CanLogIn(response.data.User._id)
            history.push('/App')
          }
    }
        
    



    function handleFileUpload(e) {
        setPicture(e.target.files[0])
      }

    return (
        <div className="LogInPage">
             <form onSubmit={handleSubmit} className='logInBorder'>
             enter your phone:  
              <input type='text' onChange={(e)=> setPhone(e.target.value)}/> 
              enter your name: 
              <input type='text' onChange={(e)=> setName(e.target.value)}/>  
              
              <input type="file" onChange={handleFileUpload} />
                <button type='submit'> Sign Up</button>
            </form>     
        </div>

    )
}

export default Register
