import React, { useContext } from 'react'
import { useState ,useEffect} from 'react'
import axios from 'axios'
const UserContext = React.createContext()

export function useUser() {
  return useContext(UserContext)
}

export  function UserProvider({ children }) {
  
  const [contacts, setContacts] = useState([])
  const [info,setInfo] =useState({})
  const [config,setConfig] =useState('')
  const[createdFlag,setCreatedFlag] =useState(0)

  useEffect(async() =>{

    let UpdatedUser = info
    UpdatedUser={...info,contacts}
    try{ 
      await axios.put("http://localhost:5000/api/logIn/" + info.id,UpdatedUser,sessionStorage['config'])
      } catch(err){console.log(err)}
  }, [createdFlag])

  async function createContact(phone) {

    if(phone==info.phone)
    {
      console.log('cant Add yourself')
       return;
    }

    let checkIfExists = contacts.filter(contact=> contact.phone==phone)

    if(checkIfExists.length != 0)
    {
      console.log('already exists')
      return;
    }

   try{ 
      const response= await axios.get("http://localhost:5000/api/logIn/" + phone,sessionStorage['config'])
      const contact = {id:response.data._id,phone:response.data.phone,name:response.data.name,image:response.data.imageName}
      setCreatedFlag(prevCreatedFlag => {return !prevCreatedFlag})
      setContacts(prevContacts => { return   [...prevContacts,  contact]})



    } catch(err){console.log('problem')}
   




  }

  return (
    <UserContext.Provider value={{setContacts,setConfig,setInfo,info,config,contacts,createContact }}>
      {children}
    </UserContext.Provider>
  )
}
