import { Avatar} from '@material-ui/core';
import React from 'react'
import { useConversations } from '../contexts/conversationsprovider';
import { useSocket } from '../contexts/socketprovider';
import '../css/conversations.css'

export default function Conversations() {
    const {setSelectedConversation,conversations} =useConversations()
    const ConnectedUsers = useSocket()
  
const handleSelectedConversation= (conversation) =>
{
  if(conversation.isGroup == false)
  {
    let userIsConnected = ConnectedUsers.find(userConnected => userConnected.userId == conversation.Participants[0].id)
    if(userIsConnected)
       conversation.connected = true
    else conversation.connected=false

  }
  setSelectedConversation(conversation)

}
  return (
          <div className='Chats' >
            {console.log(conversations)}
              {conversations.map((conversation,index)=>
              {
                 return  ( <div key={index}  onClick={()=>handleSelectedConversation(conversation)}  className='ChatsListItem'>
                     <Avatar src={process.env.PUBLIC_URL + conversation.ConversationImage}/>
                     <div className='chatInfo'>
                     <span   className='chatName' ><h2>{conversation.Name}</h2> </span>
                     <span> {conversation.LastMessage.message} </span>
                     </div>
                 <br/>
                  </div>)
                    
                })}

                </div>
          

  )
}
