import React from 'react'
import { useConversations } from '../contexts/conversationsprovider';

function ChatBody() {

    const {selectedConversation} = useConversations()
    return (
        <div className='chat_body'>
        {selectedConversation.Messages.map((message,index)=>
        {
            const lastMessage = selectedConversation.Messages.length -1 === index
            let sender = message.id==sessionStorage['id']? 'chat_message' : ' chat_message chat_reciever'

            return (
           
             <p className={sender} ref={lastMessage ? setRef : null}>

            <span className='sender_name'>{message.name}</span>
            <p className='message_time_and_content'><span className='message_content'>{message.message}</span> 
            <span className='message_time'>{(message.timeSent).substring(11,16)}</span> </p>
             </p>
       
            )}
         )}

    </div>
    )
}

export default ChatBody
