import React,{useState} from 'react'
import '../css/login.css'
import {v4 as uuidV4} from 'uuid'


export default function LogIn(props) {

    const [phone,setPhone] = useState('');
    const [UserName,setUserName] = useState('');



    /*get user information and token from DB*/
    async function handleSubmit(e)
    {
        let obj = {name: UserName,phone: phone}
        let user = await axios.post("http://localhost:5000/api/logIn",obj)

 
        if(user.data !== "not found")
        {
           sessionStorage["token"]  =user.data.token
           sessionStorage["name"]=user.data.User.name
           sessionStorage["phone"]=user.data.User.phone
           sessionStorage["authenticate"] = true

           props.GetLoginID(phone)
        }
        
        else
           setError("One or more of your identification details is incorrect.")
    }

    function pictureUpload()
    {

    }

    return (
        <div className="LogInPage">
            <form onSubmit={handleSubmit} className='logInBorder'>
             enter your phone:  
              <input type='text' onChange={(e)=> setPhone(e.target.value)}/> 
              enter your name: 
              <input type='text' onChange={(e)=> setUserName(e.target.value)}/>  
              <button type='submit'> Login</button>
              <button onClick={pictureUpload}> Upload Picture</button>
            </form>     
        </div>
    )
}
