import React, { useContext ,useCallback} from 'react'
import { useState,useEffect } from 'react'
import { useUser } from './userprovider'
import { useSocket } from './socketprovider'
import axios from 'axios'

const ConversationsContext = React.createContext()

export function useConversations() {
  return useContext(ConversationsContext)
}

export function ConversationsProvider({id, children }) {
  const {contacts,info} = useUser()
  const [conversations,setConversations] =useState([])
  const [selectedConversation,setSelectedConversation] = useState()
  const [createGroupFlag,setCreateGroupFlag] =useState(false)
  const socket = useSocket()
  const config= {'headers': {'x-access-token':sessionStorage['config']}};

    
  useEffect(async () =>
  {
     try{
      let response = await axios.get("http://localhost:5000/api/conversations/UserConversations/"+sessionStorage['id'],config)
      let ConversationsList =  response.data.map(conversation=>
        {
          if(conversation.Participants.length == 1 && conversation.Name == sessionStorage['name'] )
          {
              let UpdatedNameConversation = conversation
              UpdatedNameConversation= {...UpdatedNameConversation,Name:conversation.Participants[0].name}

              return UpdatedNameConversation
        
            
          }

          else return conversation

          
        })
        
      setConversations(ConversationsList)
     }catch(err){console.log(err)}


  },[setConversations,createConversation]) 

  
  async function createConversation(ids,name,image) 
  {
    let  ConversationImage= image
    if(ids.length == 0)
    {
     console.log('no participants choosen')
     return;
    }
     let participants =ids.map(id=>
    { 
      let  addContactToConversation =contacts.filter(contact=> id === contact.id)    
      return addContactToConversation[0]
    })


    participants=[...participants,{id:info.id,name:info.name,phone:info.phone,image:info.ProfilePic}]
    
    if(ids.length > 1)
    {
      const formData= new FormData();
      formData.append('file',ConversationImage)
      const fileResponse =await axios.post("http://localhost:5000/api/conversations/AddConversationPic",formData,{headers: {
      'Content-Type': 'multipart/form-data'}})
      const {fileName,filePath} =fileResponse.data
      ConversationImage='uploads/'+fileName
    }


    //updateDB
    let newConversation = {name:name,creatorId: sessionStorage['id'],participants:participants,messages:[],lastMessage:{id:'',sender:'',message:''},ConversationImage:ConversationImage}
    try{
       await axios.post("http://localhost:5000/api/conversations",newConversation,config)
    } catch(err){console.log(err)}

  }


  
  const addMessageToConversation =async ({currentConversation, message, fromMe, sender}) =>
  {
 
    if(fromMe)
    {
      let CurrentMessage ={ id: sender.id, name: sender.name,message: message}
      let senderParticipant = {id:info.id,name:info.name,phone:info.phone,image:info.ProfilePic}
      let updatedConversation={...currentConversation,LastMessage : CurrentMessage,Messages: [...currentConversation.Messages , CurrentMessage]}
      setSelectedConversation(updatedConversation)
      let updateDBConversation = {...currentConversation,Participants: [...conversation.Participants, senderParticipant]}
      let response = await axios.put("http://localhost:5000/api/conversations/" + conversation._id,updateDBConversation,config)
    }
      
  }

  function GotNewMessage() 
  {
    setNewMessageFlag(!newMessageFlag)
  }
  useEffect(() => {
    if (socket == null) return

    socket.on('receive-message', GotNewMessage)

    return () => socket.off('receive-message')
  }, [socket])


  function sendMessage(currentConversation,text) {

    let sender ={id: sessionStorage['id'], name: info.name}

    socket.emit('send-message', {currentConversation:currentConversation ,message:text,sender:sender})
    addMessageToConversation({currentConversation:selectedConversation,message:text ,fromMe: true,sender:sender})
  }



  return (
    <ConversationsContext.Provider value={{createGroupFlag,setCreateGroupFlag,sendMessage,conversations,createConversation,setConversations,setSelectedConversation,selectedConversation}}>
      {children}
    </ConversationsContext.Provider>
  )
}
