import React, { useContext } from 'react'
import { useState } from 'react'
const ContactsContext = React.createContext()

export function useContacts() {
  return useContext(ContactsContext)
}

export  function ContactsProvider({ children }) {
  const [contacts, setContacts] = useState([])

  async function createContact(phone) {

    const response = await axios.get("http://localhost:5000/api/logIn/" + phone)
    const contact = {id:response.data._id,phone:response.data.phone,name:response.data.name,image:response.data.imageName}
    setContacts(prevContacts => {
      return [...prevContacts,  contact]
    })
  }

  return (
    <ContactsContext.Provider value={{ contacts, createContact }}>
      {children}
    </ContactsContext.Provider>
  )
}
