import React from 'react'
import { useUser } from '../contexts/userprovider'
import { useConversations } from '../contexts/conversationsprovider'
import {Button,Avatar} from '@material-ui/core';
import '../css/addnew.css'
import '../css/addnewconversation.css'

function AddContactConversation( { closeModal }) {

    const { contacts } = useUser()
    const { createConversation,setCreateGroupFlag } = useConversations()
    
    function handleSubmit({id,name,image}) 
    {
      let ids=[id]
      createConversation(ids,name,image)
      closeModal()
    }
    
    return (
      <div className='add_new_chat_border'>
     <div className='new_conversation' >
       <div className='button_and_title'>
        <button className='add_new_button new_group' onClick ={ ()=> setCreateGroupFlag(true)}> create new group</button>
   
        <h3 className='add_contact_title start_conversation'> start new chat : </h3>
       </div >
       <div className='contacts'>
        {contacts.map((contact,index) =>
          {
            return(
               <div key={index} onClick={()=>handleSubmit({id:contact.id,name:contact.name,image:contact.imageName})} className='contactItem'>
                   <Avatar  src={process.env.PUBLIC_URL + contact.imageName}   fontSize='large'/>
                   <div className='contactInfo'> <h2 style={{fontWeight:'400'}}>{contact.name} </h2> </div>
               </div>)})}
       </div>
      </div>
      </div>

    )
}

export default AddContactConversation
