import '../css/dashboard.css'
import React from 'react'
import SideBar from './sidebar'
import Chat from './chat'
import axios from 'axios'
import { useEffect } from 'react'
import { useConversations } from '../contexts/conversationsprovider'
import { useUser } from '../contexts/userprovider'

export default function Dashboard({id}) {
    const {selectedConversationDetails} =useConversations()
    const {info,setInfo,setContacts} =useUser()

 
    useEffect(async function fetchData()
    {
        const response = await axios.get("http://localhost:5000/api/logIn/"+id,{'headers': {'x-access-token':sessionStorage['config']}});
        setInfo({id:response.data._id,name:response.data.name,phone:response.data.phone,ProfilePic:response.data.imageName})
        setContacts(response.data.contacts)
         
     }, []);


    

    return (
        <div className='dashboard'>
       
           <div className='dashboard_body'>
              <SideBar id={id}/>
              {selectedConversationDetails!==undefined?  <Chat id={id}  />: ''}
           </div>
        </div>
    )
}
