import React, { useState ,useEffect, useContext } from 'react'
import axios from 'axios'
const UserContext = React.createContext()
import { useSocket } from "./socketprovider";

export function useUser() {
  return useContext(UserContext)
}

export  function UserProvider({ children })
{
  const [contacts, setContacts] = useState([])
  const [info,setInfo] =useState({})
  const config= {'headers': {'x-access-token':sessionStorage['config']}}
  const {ConnectedUsers } = useSocket();
  

  useEffect(async ()=>
  {
      const response = await axios.get("http://localhost:5000/api/logIn/"+sessionStorage['id'],config);
      setInfo({id:response.data._id,name:response.data.name,phone:response.data.phone,imageName:response.data.imageName,LastSeen:response.data.LastSeen})
      setContacts(response.data.contacts)
       
   }, [setInfo]);

   useEffect(async () =>
   {
     setContacts(prevContacts=>
    {
      prevContacts.map(async (contact) =>
      {
        let userIsConnected = ConnectedUsers.some(user => user.userId == contact.id)
        if(userIsConnected)
        {
          contact.connected= true  
          return contact;
        }

        else
        {
          const response = await axios.get("http://localhost:5000/api/logIn/"+contact.id,config)
          let updatedContact= {...response.data, connected:false}
          return updatedContact;
        }        

      })
    })
    
   },[ConnectedUsers])


  async function createContact(phone)
  {
    if(phone==info.phone)
    {
      console.log('cant Add yourself')
       return;
    }

    let checkIfExists = contacts.filter(contact=> contact.phone==phone)

    if(checkIfExists.length != 0)
    {
      console.log('already exists')
      return;
    }

    try{ 

      const response= await axios.get("http://localhost:5000/api/logIn/getByPhone/" + phone,config)

      if(response != 'no such user')
      {
      const contact = {id:response.data._id,phone:response.data.phone,name:response.data.name,image:response.data.imageName,LastSeen:response.data.LastSeen}
      let newContacts = [...contacts,contact]
      setContacts(prevContacts=> {return [...prevContacts,contact]})
      let UpdatedUser = info
      UpdatedUser={...info,contacts:newContacts}
       try{
        await axios.put("http://localhost:5000/api/logIn/" + sessionStorage['id'],UpdatedUser,{'headers': {'x-access-token':sessionStorage['config']}})
       } catch(err){console.log(err)}
      }
      else console.log('user dosent exist')
     
    } catch(err){console.log(err)}
  }

  return (
    <UserContext.Provider value={{setContacts,setInfo,info,config,contacts,createContact }}>
      {children}
    </UserContext.Provider>
  )
}
