  import '../css/addnew.css'
import AddContactConversation from './addcontactconversation';
import CreateNewGroup from './createnewgroup';
import { useConversations } from '../contexts/conversationsprovider'

export default function NewConversationModal({ closeModal }) {
    const {createGroupFlag } = useConversations()
   



  return (
    <div className='add_new'>
    
          {createGroupFlag?  <CreateNewGroup closeModal={closeModal}/> :<AddContactConversation  closeModal={closeModal}/>}

      </div>
    
  )
}
