import React, { useContext ,useCallback} from 'react'
import { useState,useEffect } from 'react'
import { useUser } from './userprovider'
import { useSocket } from './socketprovider'
import axios from 'axios'

const ConversationsContext = React.createContext()

export function useConversations() {
  return useContext(ConversationsContext)
}

export function ConversationsProvider({id, children }) {
  const {contacts,info} = useUser()
  const [conversations,setConversations] =useState([])
  const [selectedConversation,setSelectedConversation] = useState()
  const [createGroupFlag,setCreateGroupFlag] =useState(false)
  const socket = useSocket()
  const config= {'headers': {'x-access-token':sessionStorage['config']}};

  useEffect(async () =>
  {
    try
    {
      let response = await axios.get("http://localhost:5000/api/conversations/UserConversations/"+sessionStorage['id'],config)
      let ConversationsList =  response.data.map(conversation=>
      {
        if(conversation.Participants.length == 1 && conversation.Name == sessionStorage['name'] )
        {
          let UpdatedNameConversation = conversation
          UpdatedNameConversation= {...UpdatedNameConversation,Name:conversation.Participants[0].name}
  
          return UpdatedNameConversation
        }
  
        else return conversation
          
      })
   /*  if(ConversationsList.length>0)
      setSelectedConversation(ConversationsList[0])*/
        
      setConversations(ConversationsList)
  
     }catch(err){console.log(err)}

  },[]) 

async function createConversation(ids,name,image) 
  {

    let  ConversationImage= image
    let isGroup = false
    if(ids.length == 0)
    {
     console.log('no participants choosen')
     return;
    }
     const participants =ids.map(id=>
    { 
      let  addContactToConversation =contacts.filter(contact=> id === contact.id)    
      return addContactToConversation[0]
    })
    
    if(ids.length > 1)
    {
      const formData= new FormData();
      formData.append('file',ConversationImage)
      const fileResponse =await axios.post("http://localhost:5000/api/conversations/AddConversationPic",formData,{headers: {
      'Content-Type': 'multipart/form-data'}})
      const {fileName,filePath} =fileResponse.data
      ConversationImage='uploads/'+fileName
      isGroup = true
    }

    let newConversation = {Name:name,creatorId: sessionStorage['id'],Participants:participants,Messages:[],LastMessage:{id:'',sender:'',message:''},ConversationImage:ConversationImage,isGroup}
    
    let DBparticipants=[...participants,{id:info.id,name:info.name,phone:info.phone,image:info.ProfilePic}]
    let DBconversation = {...newConversation,Participants:DBparticipants}



    //updateDB
   
    try{
       let Response = await axios.post("http://localhost:5000/api/conversations",DBconversation,config)
       if(Response.data.status == "created")
       {
       newConversation= {...newConversation,_id:Response.data.conversation._id}
       setSelectedConversation(newConversation)
       setConversations(prevConversations => { return [...prevConversations, newConversation]})
       }
    } catch(err){console.log(err)}
    

  }



const addMessageToConversation = useCallback(async({updatedConversations}) =>
{
  console.log(sessionStorage['name'])
    setConversations(()=>
    {
      let ConversationsList =  updatedConversations.map(conversation=>
        {
          if(selectedConversation)
          {
            if(conversation._id == selectedConversation._id)
              setSelectedConversation(conversation)
          }

          if(conversation.Participants.length == 1 && conversation.Name == sessionStorage['name'] )
          {
            let UpdatedNameConversation = conversation
            UpdatedNameConversation= {...UpdatedNameConversation,Name:conversation.Participants[0].name,ConversationImage:conversation.Participants[0].image}
    
            return UpdatedNameConversation
          }
    
          else return conversation   
        })

        return ConversationsList 
      
    })
  
}, [setConversations,selectedConversation,setSelectedConversation])


  function sendMessage(text) 
  {
    let senderParticipant = {id:info.id,name:info.name,phone:info.phone,image:info.ProfilePic}
    let CurrentMessage ={ id: sessionStorage['id'], name: info.name,message: text}
     let SendToContactsConversation = {Name:selectedConversation.Name,creatorId:selectedConversation.creatorId,Participants: selectedConversation.Participants,Messages: [...selectedConversation.Messages,CurrentMessage] 
    ,LastMessage:CurrentMessage,ConversationImage:selectedConversation.ConversationImage,isGroup:selectedConversation.isGroup}
    socket.emit('send-message', {Conversation:SendToContactsConversation,id:selectedConversation._id,senderParticipant:senderParticipant})
  }

  useEffect(() => {
    if (socket == null) return

    socket.on('receive-message', addMessageToConversation)

    return () => socket.off('receive-message')
  }, [socket, addMessageToConversation])






  return (
    <ConversationsContext.Provider value={{createGroupFlag,setCreateGroupFlag,sendMessage,conversations,createConversation,setConversations,setSelectedConversation,selectedConversation}}>
      {children}
    </ConversationsContext.Provider>
  )
}
