import '../css/chat.css'
import React, { useRef } from 'react'
import { useState ,useEffect,useCallback} from 'react'
import { useConversations } from '../contexts/conversationsprovider';
import { Avatar, IconButton, useForkRef } from '@material-ui/core'
import InsertEmoticonIcon from '@material-ui/icons/InsertEmoticon';
import MicIcon from '@material-ui/icons/Mic';
import Picker from 'emoji-picker-react';




export default function Chat() {

    const [Text,setText] = useState('')
    const {sendMessage,selectedConversation} = useConversations()
    const inputRef = useRef(null)
  
    const [emojiFlag,setEmojiFlag] = useState(false)


    const setRef = useCallback((node) => {
        if(node)
          node.scrollIntoView({smooth:true})
        },[])
    
    const groupheader = () =>
    {
    console.log('Group!')
    }

    const privateConversationHeader = () =>
    {
        console.log(selectedConversation.Participants[0].connected)
       if(selectedConversation.Participants[0].connected)
       {
          return( <div className='connected'><div className='circle'></div> connected </div>)
       }
       else return (<div>{selectedConversation.Participants[0].LastSeen}</div>)

    }

    function handleSubmit(e)
    {
      e.preventDefault()
      sendMessage(Text)
      setText(' ')
    }

    const onEmojiClick = (event, emojiObject) => {
        setText(Text + emojiObject.emoji)
        setEmojiFlag(false)
      };




    return (
        <div className='chat'>
            <div className='chat_header'>
                <Avatar src={process.env.PUBLIC_URL +selectedConversation.ConversationImage}/>
                <div className='chat_header_info'>
                   <h2> {selectedConversation.Name} </h2> 
                   {selectedConversation.isGroup?  groupheader() : privateConversationHeader()}
               </div>
           </div>
    
           <div className='chat_body'>
               {selectedConversation.Messages.map((message,index)=>
               {
                   const lastMessage = selectedConversation.Messages.length -1 === index
                   let sender = message.id==sessionStorage['id']? 'chat_message' : ' chat_message chat_reciever'

                   return (
                  
                    <p className={sender} ref={lastMessage ? setRef : null}>

                   <span className='sender_name'>{message.name}</span>
                   <span className='message_content'>{message.message}</span> </p>
              
                   )}
                )}

           </div>

           <div className='chat_footer'>
               <IconButton  onClick={()=>setEmojiFlag(!emojiFlag)}>
                 <InsertEmoticonIcon fontSize='large'/>
                </IconButton>
                {emojiFlag?  <Picker onEmojiClick={(e,emojiObject)=>onEmojiClick(e,emojiObject)} pickerStyle={{ width: '100%' }}/>: ''}
                 <form onSubmit={handleSubmit} className='message_section'>
                    <input className='message_input' ref={inputRef}  value={Text} onChange={(e)=>setText(e.target.value)} type='text' placeholder='type a message'/>
                    <button type='submit' > send </button>
                 </form>
                 <IconButton>
                  <MicIcon fontSize='large'/>
               </IconButton>
           </div>
        </div>
    )
}
